import random

def get_random_str(main_str, substr_len):
    idx = random.randrange(0, len(main_str) - substr_len + 1)    # Randomly select an "idx" such that "idx + substr_len <= len(main_str)".
    return main_str[idx : (idx+substr_len)]

def phiX174_genome():
#    main_str=''
    main_str= "AGCTTGAGATTCAGATGGCTATGCGTAACT"
    reads_list = []
    
#    for i in range(1618):
    for i in range(1000000):
        if(len(reads_list) == 27):
            return reads_list
        
        read = get_random_str(main_str, 20)
#        if read not in reads_list:
        if read not in reads_list:
            reads_list.append(str(read.strip())) 
        
    return reads_list
    
